import React, { useState } from "react";
import { toast } from "react-hot-toast";
import { MdOutlineFileUpload } from "react-icons/md";
import { ImagetoBase64 } from "../utility/ImagetoBase64";

export const NewProduct = () => {
  const [data, setData] = useState({
    name: "",
    category: "",
    image: "",
    price: "",
    description: "",
  });

  const handleOnChange = (e) => {
    const { name, value } = e.target;
    setData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const uploadImage = async (e) => {
    const selectedFile = e.target.files[0];
    if (!selectedFile) {
      console.error("No file selected");
      return;
    }

    const blob = new Blob([selectedFile]);

    try {
      const imageDataURL = await ImagetoBase64(blob);
      setData((prevData) => ({
        ...prevData,
        image: imageDataURL,
      }));
    } catch (error) {
      console.error("Error converting image to base64:", error);
      toast.error("Failed to upload image");
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log(data);
  };

  return (
    <div className="p-4">
      <form
        className="m-auto w-full max-w-md shadow flex flex-col p-3 bg-white"
        onSubmit={handleSubmit}
      >
        {/* Your form inputs */}
        <label htmlFor="name">Name</label>
        <input
          type={"text"}
          name="name"
          className="bg-slate-200 p-1 my-1"
          onChange={handleOnChange}
          value={data.name}
        />
        {/* More form inputs... */}

        {/* Image input */}
        <label htmlFor="image">
          Image
          <div
            id="image"
            className="h-40 w-full bg-slate-300 rounded flex items-center justify-center"
          >
            {data.image ? (
              <img src={data.image} className="h-full" alt="Product" />
            ) : (
              <span className="text-5xl">
                <MdOutlineFileUpload />
              </span>
            )}
            <input
              type={"file"}
              accept="image/*"
              id="image"
              onChange={uploadImage}
              className="hidden"
            />
          </div>
        </label>

        {/* Submit button */}
        <button className="bg-slate-500 h-10 rounded hover:bg-slate-800 text-white text-lg font-medium my-2 drop-shadow">
          Save
        </button>
      </form>
    </div>
  );
};
