import React from "react";
import { Link } from "react-router-dom";
import { BsCart4 } from "react-icons/bs";
import { ImBooks } from "react-icons/im";
import { MdFavorite } from "react-icons/md";
import { TbLogout } from "react-icons/tb";
import { TbLogout2 } from "react-icons/tb";
import { MdAdd } from "react-icons/md";
import { HiOutlineUserAdd } from "react-icons/hi";
import { useDispatch, useSelector } from "react-redux";
import { logoutRedux } from "../redux/userSlice";
import toast from "react-hot-toast";

export const Header = () => {
  const userData = useSelector((state) => state.users);
  console.log(userData);

  const dispatch = useDispatch();

  const handleLogout = () => {
    dispatch(logoutRedux());
    toast("Logout Successfully")
  };
  console.log(process.env)
  return (
    <header className="w-full fixed shadow-md items-center z-50 h-20  bg-indigo-50">
      <div className=" flex justify-between items-center mt-6 ">
        <Link to={"/"}>
          <div className="ml-4 logo">
            <span className="text-black-200 text-2xl font-bold flex justify-start items-center">
              <ImBooks />
              BookManagementSystem
            </span>
          </div>
        </Link>
        <div className="nav-items flex gap-4 items-center  md:gap-7 mr-4">
          {userData.fullname ? (
            <span className="text-xl cursor-pointer text-grey-200 font-bold  hover:text-indigo-200">
              Hello! {userData.fullname}
            </span> 
          ) : (
            <span className="text-xl cursor-pointer text-grey-200 font-bold  hover:text-indigo-200">
              Hello! Guest
            </span>
          )}
          
          <Link to={"cart"}>
            <div className=" text-2xl cursor-pointer relative text-grey-200 font-bold    hover:text-indigo-200">
              <BsCart4 />
              <div className="absolute -top-3 -right-2 bg-red-500 text-sm text-center h-5 w-5 rounded-full m-0 p-0">
                0
              </div>
            </div>
          </Link>
          <Link to={"favourite"}>
            <div className="text-2xl cursor-pointer text-grey-200 font-bold  hover:text-indigo-200">
              <MdFavorite />
            </div>
          </Link>
          <Link to={"newProduct"}>
            <div className="text-2xl cursor-pointer text-grey-200 font-bold  hover:text-indigo-200">
              <MdAdd />
            </div>
          </Link>
          <div className="text-2xl cursor-pointer text-grey-200 font-bold  hover:text-indigo-200" onClick={handleLogout}>
          <TbLogout2 />
          </div>

          <Link to={"register"}>
            <div className="text-2xl cursor-pointer text-grey-200 font-bold   hover:text-indigo-200">
              <HiOutlineUserAdd />
            </div>
          </Link>
          <Link to={"login"}>
            <div className="text-2xl cursor-pointer text-grey-200 font-bold   hover:text-indigo-200">
              <TbLogout />
            </div>
          </Link>


          
        </div>
      </div>
    </header>
  );
};
