import React, { useState } from "react";
import { Link, use } from "react-router-dom";
export const Login = () => {
  const navigate = useNavigate();
  const [data, setData] = useState({
   
    email: "",   
    password: "",
  });
  console.log(data);

  const handleOnChange = (e) => {
    const { name, value } = e.target;
    setData((preve) => {
      return {
        ...preve,
        [name]: value,
      };
    });
  };
  const handleSubmit = (e) => {
    e.preventDefault();
    const {  email, password } = data;
    if (email && password) {
      
        alert("success");
    } else{
      alert("Please fill all the fields")
    }
  };
  return (
    <div className="flex justify-center">
      <div className="lg:w-3/12 mt-8 mb-8 p-10 shadow-lg mx-auto">
        <h3 className="text-3xl text-center font-semibold py-5">Login Form</h3>
        <form className="flex flex-col" onSubmit={handleSubmit}>
          <div className="flex flex-col mt-4 mb-1">
            <input
              type={"email"}
              id="email"
              name="email"
              onChange={handleOnChange}
              placeholder="your Email ***@example.com"
              className="p-2 border border-gray-300 rounded"
            />

            <span className="text-red-500">
              Email is <strong>required</strong>
            </span>
          </div>
          <div className="flex flex-col mt-4 mb-1">
            <input
              className="p-2 border border-gray-300 rounded"
              type={"password"}
              id="password"
              onChange={handleOnChange}
              name="password"
              placeholder="Your Password"
            />

            <span className="text-red-500">
              Password is <strong>required</strong>
            </span>
          </div>
          <button
            type="submit"
            className="bg-indigo-600 p-2 rounded mt-3 text-white"
          >
            Login
          </button>
            
        </form>
        <div className="flex justify-between mt-5">
          <span className="text-indigo-600 ml-2 cursor-pointer">
            Forget Password
          </span>
          <Link to={"/register"}>
            <span className="text-indigo-600 ml-2 cursor-pointer">Signup</span>
          </Link>
        </div>
      </div>
    </div>
  );
};
